sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue != oldValue) {
        var livroEstanteSysId = g_form.getValue('u_livro_da_estante');

        var ga = new GlideAjax('utilScript');
        ga.addParam('sysparm_name', 'GetTotalPaginas');
        ga.addParam('sysparm_livro_estante_sysid', livroEstanteSysId);

        ga.getXMLAnswer(function(response) {
            var totalPaginas = parseInt(response);
            var paginaAtual = parseInt(newValue);

            if (paginaAtual > totalPaginas) {
				g_form.setValue('u_quantidade_de_paginas', totalPaginas);
                g_form.showFieldMsg('u_quantidade_de_paginas', 'A página atual não pode ser maior que o total de páginas do livro', 'error');
            } else {
                g_form.hideFieldMsg('u_quantidade_de_paginas');
            }
        });
    }
}
