sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue) {
        debugger;
        // Chama a função server-side via GlideAjax para buscar o livro
        var ga = new GlideAjax('utilScript');
        ga.addParam('sysparm_name', 'GetLivro'); // Chama o método server-side
        ga.addParam('sysparm_titulo_livro', newValue);

        ga.getXMLAnswer(function(response) {
			debugger;
			var livro = JSON.parse(response);
            if (livro) {
				debugger;
                g_form.setValue('u_livro', livro.sys_id, livro.titulo);
            }
        })
    }

}
